transfer-by-account.reduce:
function (keys, values, rereduce) {
    if (rereduce) {
        return values.reduce((a, b) => {
            return {
                amount: parseFloat(a.amount) + parseFloat(b.amount),
                count: a.count + b.count
            };
        });
    } else {
        // BigInt is not supported by CouchDB 3.2.0, so use float
        let s = 0;
        values.forEach(v => {
            s += parseFloat(v);
        });
        return { amount: s, count: values.length };
    }
}

transfer-by-account.map:
function (doc) {
  if (doc.docType === 'event' && doc.topics === 'Transfer') {
    let k = [doc.address, doc.data.from, doc.data.to];
    emit(k, doc.data.value);
  }
}

