transfer-by-date.map:
function (doc) {
  if (doc.docType === 'event' && doc.topics === 'Transfer' && doc.block_timestamp) {
    let d = new Date(doc.block_timestamp);
    let k = [doc.address, d.getUTCFullYear(), d.getUTCMonth()+1, d.getUTCDate(), d.getUTCHours(), d.getUTCMinutes()];
    emit(k, doc.data.value);
  }
}
