count-by-topic-date.map:
function (doc) {
  if (doc.docType === 'event' && doc.topics && !Array.isArray(doc.topics) && doc.block_timestamp) {
    let d = new Date(doc.block_timestamp);
    let k = [doc.topics, d.getUTCFullYear(), d.getUTCMonth()+1, d.getUTCDate(), d.getUTCHours(), d.getUTCMinutes()];
    emit(k, 1);
  }
}

