by-contract.index:
function(doc) {
    index('default', doc._id);
    if (doc.to_address) {
        index('to_address', doc.to_address, { 'store': true });
    }
    if (doc.block_timestamp) {
        index('block_timestamp', doc.block_timestamp, { 'store': true });
    }
    if (doc.docType) {
        index('docType', doc.docType, { 'store': true });
    }
    if (doc.input.method) {
        index('method', doc.input.method, { 'store': true });
    }
}
