count-by-method-date.map:
function (doc) {
  if (doc.docType === 'transaction' && doc.to_address && doc.input.method && doc.block_timestamp) {
    let d = new Date(doc.block_timestamp);
    let k = [doc.to_address, doc.input.method, d.getUTCFullYear(), d.getUTCMonth()+1, d.getUTCDate(), d.getUTCHours(), d.getUTCMinutes()];
    emit(k, 1);
  }
}

collated-events.map:
function (doc) {
  if (doc.docType === 'transaction') {
    emit([doc.hash, 0], doc.input.method);
  } else if (doc.docType === 'event') {
    emit([doc.transaction_hash, 1], doc.topics);
  }
}

count-by-contract-date.map:
function (doc) {
    if (doc.docType === 'transaction' && doc.to_address && doc.block_timestamp) {
    let d = new Date(doc.block_timestamp);
    let k = [doc.to_address, d.getUTCFullYear(), d.getUTCMonth()+1, d.getUTCDate(), d.getUTCHours(), d.getUTCMinutes()];
    emit(k, 1);
  }
}

transfer-by-date.reduce:
function (keys, values, rereduce) {
    if (rereduce) {
        return values.reduce((a, b) => {
            return {
                amount: parseFloat(a.amount) + parseFloat(b.amount),
                count: a.count + b.count
            };
        });
    } else {
        // BigInt is not supported by CouchDB 3.2.0, so use float
        let i = 0;
        values.forEach(v => {
            i += parseFloat(v.amount);
        });
        return { amount: i, count: values.length };
    }
}

transfer-by-date.map:
function (doc) {
    if (doc.docType === 'transaction' && doc.to_address && doc.input.method && doc.input.method === 'transfer' && doc.block_timestamp) {
        let d = new Date(doc.block_timestamp);
        let k = [doc.to_address, d.getUTCFullYear(), d.getUTCMonth() + 1, d.getUTCDate(), d.getUTCHours(), d.getUTCMinutes()];
        emit(k, { from: doc.from_address, to: doc.input.params._to, amount: doc.input.params._value });
    }
}
