count-by-method-date.map:
function (doc) {
  if (doc.docType === 'transaction' && doc.input.method && doc.block_timestamp) {
    let d = new Date(doc.block_timestamp);
    let k = [doc.input.method, d.getUTCFullYear(), d.getUTCMonth()+1, d.getUTCDate(), d.getUTCHours(), d.getUTCMinutes()];
    emit(k, 1);
  }
}

collated-events.map:
function (doc) {
  if (doc.docType === 'transaction') {
    emit([doc.hash, 0], doc.input.method);
  } else if (doc.docType === 'event') {
    emit([doc.transaction_hash, 1], doc.topics);
  }
}
